def user2name(user):
    name = user.first_name
    if user.last_name:
        name += ' %s' % user.last_name
    if user.username:
        name += ' (@%s)' % user.username

    return name

def html_sanitize_telegram(html):
    allowed_tags = set({"b", "i", "u", "s", "a", "code", "pre"})
    cleaner = Cleaner(safe_attrs_only=True, safe_attrs=set(), allow_tags=allowed_tags, remove_unknown_tags=False)
    html = cleaner.clean_html(html)
    # remove surrounding div
    return html[5:-6]

def handle_db(records): # records are instances of mail.message for r in records:
if not html2plaintext(r.body or "").startswith("/telegram"): continue lead = env["crm.lead"].browse(r.res_id)
contact_name = lead.contact_name or "" if not contact_name.endswith("@telegram"): continue telegram_user_id = contact_name.split("@telegram")[0] telegram_message_html = html_sanitize_telegram(r.body) telegram_message_html = telegram_message_html.replace("/telegram", "") telegram.sendMessage(telegram_user_id, telegram_message_html)
odoo_message_text = "%s:\n\n%s" % (params.TELEGRAM_MESSAGE_SENT, telegram_message_html) lead.message_post(body=odoo_message_text)
